typedef struct StackChar  
{  
	char c;   
	struct StackChar *next;   
}SC;       //StackCharç±»å‹çš„ç»“ç‚¹SC  

typedef struct StackFloat  
{  
	float f;   
	struct StackFloat *next;   
}SF;       //StackFloatç±»å‹çš„ç»“ç‚¹SF  

SC *SC_Push(SC *s,char c)          //SCç±»å‹çš„æŒ‡é’ˆPushï¼Œè¿”å›p  
{  
	SC *p=(SC*)malloc(sizeof(SC));   
	p-&gt;c=c;   
	p-&gt;next=s;   
	return p;   
}   

SF *SF_Push(SF *s,float f)        //SFç±»å‹çš„æŒ‡é’ˆPushï¼Œè¿”å›p  
{  
	SF *p=(SF*)malloc(sizeof(SF));   
	p-&gt;f=f;   
	p-&gt;next=s;   
	return p;   
}   

SC *SC_Pop(SC *s)    //SCç±»å‹çš„æŒ‡é’ˆPop  
{  
	SC *q=s;   
	s=s-&gt;next;   
	free(q);   
	return s;   
}   

SF *SF_Pop(SF *s)      //SFç±»å‹çš„æŒ‡é’ˆPop  
{  
	SF *q=s;   
	s=s-&gt;next;   
	free(q);   
	return s;   
}   

float Operate(float a,unsigned char theta, float b)      //è®¡ç®—å‡½æ•°Operate  
{  
	switch(theta)  
	{  
		case '+': return a+b;   
		case '-': return a-b;   
		case '*': return a*b;   
		case '/': return a/b;   
		case '^': return powf(a,b);   
		default : return 0;   
	}   
}   

char OPSET[OPSETSIZE]={'+','-','*','/','(',')','#','^'};   

Status In(char Test,char *TestOp)  
{  
	int Find=false;   
	for (int i=0; i&lt; OPSETSIZE; i++)  
	{  
		if(Test == TestOp[i])  
			Find= true;   
	}   
	return Find;   
}   

Status ReturnOpOrd(char op,char *TestOp)  
{   
	for(int i=0; i&lt; OPSETSIZE; i++)  
	{  
		if (op == TestOp[i])  
			return i;  
	}  
}  

char precede(char Aop, char Bop)  
{   
	return Prior[ReturnOpOrd(Aop,OPSET)][ReturnOpOrd(Bop,OPSET)];   
}   

