&lt;span class="hljs-keyword"&gt;A&lt;/span&gt; &amp;#61; &lt;span class="hljs-number"&gt;20&lt;/span&gt;*log10(&lt;span class="hljs-keyword"&gt;A&lt;/span&gt;)&lt;span class="hljs-comment"&gt;;  % 单位换成dB&lt;/span&gt;
figure
semilogx( f,&lt;span class="hljs-keyword"&gt;A&lt;/span&gt; )
xlabel(&amp;#39;频率/Hz&amp;#39;)
ylabel(&amp;#39;幅值增益/dB&amp;#39;)
