            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(((scanpwmtimeL&amp;#43;scanpwmtimeH)&amp;gt; (pwmPeriod&lt;span class="hljs-regexp"&gt;/2)) &amp;amp;&amp;amp;((scanpwmtimeL&amp;#43;scanpwmtimeH) &amp;lt; (pwmPeriod/&lt;/span&gt;&lt;span class="hljs-number"&gt;2&lt;/span&gt; &amp;#43; pwmPeriod)))&lt;span class="hljs-regexp"&gt;//&lt;/span&gt;判断一下高低电平时间是否符合PWM的设定
            {
                ledStep &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
                ledTwinkleEnable &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;    &lt;span class="hljs-regexp"&gt;//&lt;/span&gt;使能LED闪功能
            }
        }

        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(scanpwmtime &amp;gt; pwmPeriod*&lt;span class="hljs-number"&gt;2&lt;/span&gt;) &lt;span class="hljs-regexp"&gt;//&lt;/span&gt;当低电平持续过长说明PWM结束
        {
