     &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(b &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; b &amp;lt;&lt;span class="hljs-number"&gt;100&lt;/span&gt;;b&amp;#43;&amp;#43;);
