&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-keyword"&gt;module&lt;/span&gt; ColorLightController(
    &lt;span class="hljs-keyword"&gt;input&lt;/span&gt; clk, &lt;span class="hljs-comment"&gt;// 时钟信号&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;input&lt;/span&gt; [&lt;span class="hljs-number"&gt;1&lt;/span&gt;:&lt;span class="hljs-number"&gt;0&lt;/span&gt;] pattern_switch, &lt;span class="hljs-comment"&gt;// 花样选择信号&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;output&lt;/span&gt; &lt;span class="hljs-keyword"&gt;reg&lt;/span&gt; [&lt;span class="hljs-number"&gt;7&lt;/span&gt;:&lt;span class="hljs-number"&gt;0&lt;/span&gt;] led &lt;span class="hljs-comment"&gt;// 彩灯输出信号&lt;/span&gt;
);

&lt;span class="hljs-keyword"&gt;reg&lt;/span&gt; [&lt;span class="hljs-number"&gt;7&lt;/span&gt;:&lt;span class="hljs-number"&gt;0&lt;/span&gt;] pattern[&lt;span class="hljs-number"&gt;0&lt;/span&gt;:&lt;span class="hljs-number"&gt;3&lt;/span&gt;]; &lt;span class="hljs-comment"&gt;// 存储4种花样&lt;/span&gt;

