&lt;span class="hljs-keyword"&gt;reg&lt;/span&gt; [&lt;span class="hljs-number"&gt;2&lt;/span&gt;:&lt;span class="hljs-number"&gt;0&lt;/span&gt;] light_counter; &lt;span class="hljs-comment"&gt;// 计数器&amp;#xff0c;用于控制灯的亮灭时间&lt;/span&gt;

&lt;span class="hljs-keyword"&gt;always&lt;/span&gt; &amp;#64;(&lt;span class="hljs-keyword"&gt;posedge&lt;/span&gt; clk) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (pattern_switch &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;2&amp;#39;b00&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt; &lt;span class="hljs-comment"&gt;// 第一种花样&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (light_counter &amp;lt; &lt;span class="hljs-number"&gt;4&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
            led &amp;lt;&amp;#61; pattern[pattern_counter][&lt;span class="hljs-number"&gt;7&lt;/span&gt; - light_counter];
            light_counter &amp;lt;&amp;#61; light_counter &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
            light_counter &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (pattern_counter &amp;lt; &lt;span class="hljs-number"&gt;3&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
                pattern_counter &amp;lt;&amp;#61; pattern_counter &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
                pattern_counter &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;end&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;end&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (pattern_switch &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;2&amp;#39;b01&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt; &lt;span class="hljs-comment"&gt;// 第二种花样&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (light_counter &amp;lt; &lt;span class="hljs-number"&gt;4&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
            led &amp;lt;&amp;#61; {pattern[pattern_counter][&lt;span class="hljs-number"&gt;3&lt;/span&gt; - light_counter], pattern[pattern_counter][&lt;span class="hljs-number"&gt;4&lt;/span&gt; &amp;#43; light_counter]};
            light_counter &amp;lt;&amp;#61; light_counter &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
            light_counter &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (pattern_counter &amp;lt; &lt;span class="hljs-number"&gt;3&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
                pattern_counter &amp;lt;&amp;#61; pattern_counter &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
                pattern_counter &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;end&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;end&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (pattern_switch &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;2&amp;#39;b10&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt; &lt;span class="hljs-comment"&gt;// 第三种花样&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (light_counter &amp;lt; &lt;span class="hljs-number"&gt;4&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
            led &amp;lt;&amp;#61; {pattern[pattern_counter][&lt;span class="hljs-number"&gt;2&lt;/span&gt; - light_counter], pattern[pattern_counter][&lt;span class="hljs-number"&gt;5&lt;/span&gt; &amp;#43; light_counter]};
            light_counter &amp;lt;&amp;#61; light_counter &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
            light_counter &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (pattern_counter &amp;lt; &lt;span class="hljs-number"&gt;3&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
                pattern_counter &amp;lt;&amp;#61; pattern_counter &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
                pattern_counter &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;end&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;end&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt; &lt;span class="hljs-comment"&gt;// 第四种花样&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (light_counter &amp;lt; &lt;span class="hljs-number"&gt;4&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
            led &amp;lt;&amp;#61; {pattern[pattern_counter][&lt;span class="hljs-number"&gt;1&lt;/span&gt; - light_counter], pattern[pattern_counter][&lt;span class="hljs-number"&gt;6&lt;/span&gt; &amp;#43; light_counter]};
            light_counter &amp;lt;&amp;#61; light_counter &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (light_counter &amp;lt; &lt;span class="hljs-number"&gt;8&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
            led &amp;lt;&amp;#61; {pattern[pattern_counter][&lt;span class="hljs-number"&gt;1&lt;/span&gt; - light_counter], pattern[pattern_counter][&lt;span class="hljs-number"&gt;6&lt;/span&gt; &amp;#43; light_counter]};
            light_counter &amp;lt;&amp;#61; light_counter &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (light_counter &amp;lt; &lt;span class="hljs-number"&gt;12&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
            led &amp;lt;&amp;#61; {pattern[pattern_counter][light_counter - &lt;span class="hljs-number"&gt;7&lt;/span&gt;], pattern[pattern_counter][&lt;span class="hljs-number"&gt;8&lt;/span&gt; - light_counter]};
            light_counter &amp;lt;&amp;#61; light_counter &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
            light_counter &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (pattern_counter &amp;lt; &lt;span class="hljs-number"&gt;3&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
                pattern_counter &amp;lt;&amp;#61; pattern_counter &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt;
                pattern_counter &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;end&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;end&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;end&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;end&lt;/span&gt;

&lt;span class="hljs-keyword"&gt;initial&lt;/span&gt; &lt;span class="hljs-keyword"&gt;begin&lt;/span&gt; &lt;span class="hljs-comment"&gt;// 初始化4种花样&lt;/span&gt;
    pattern[&lt;span class="hljs-number"&gt;0&lt;/span&gt;] &amp;#61; &lt;span class="hljs-number"&gt;8&amp;#39;b11111111&lt;/span&gt;;
    pattern[&lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61; &lt;span class="hljs-number"&gt;8&amp;#39;b00000001&lt;/span&gt;;
    pattern[&lt;span class="hljs-number"&gt;2&lt;/span&gt;] &amp;#61; &lt;span class="hljs-number"&gt;8&amp;#39;b10000001&lt;/span&gt;;
pattern[&lt;span class="hljs-number"&gt;3&lt;/span&gt;] &amp;#61; &lt;span class="hljs-number"&gt;8&amp;#39;b00001111&lt;/span&gt;;
&lt;span class="hljs-keyword"&gt;end&lt;/span&gt;

&lt;span class="hljs-keyword"&gt;endmodule&lt;/span&gt;


&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;该模块接受一个时钟信号&lt;code class="language-javascript"&gt;clk&lt;/code&gt;&amp;#xff0c;一个用于选择花样的信号&lt;code class="language-javascript"&gt;pattern_switch&lt;/code&gt;&amp;#xff0c;并输出一个8位的彩灯输出信号&lt;code class="language-javascript"&gt;led&lt;/code&gt;。在该模块内部&amp;#xff0c;使用了一个包含4种花样的存储器&lt;code class="language-javascript"&gt;pattern&lt;/code&gt;、一个用于选择当前花样的计数器&lt;code class="language-javascript"&gt;pattern_counter&lt;/code&gt;以及一个用于控制灯的亮灭时间的计数器&lt;code class="language-javascript"&gt;light_counter&lt;/code&gt;。根据&lt;code class="language-javascript"&gt;pattern_switch&lt;/code&gt;的不同取值&amp;#xff0c;选择不同的花样&amp;#xff0c;使用&lt;code class="language-javascript"&gt;pattern_counter&lt;/code&gt;和&lt;code class="language-javascript"&gt;light_counter&lt;/code&gt;控制灯的亮灭时间和切换花样的时间。在初始化部分&amp;#xff0c;给出了4种花样的初始值。&lt;br /&gt;·&lt;br /&gt;请注意&amp;#xff0c;此处的代码只是一个仿真&amp;#xff0c;如果要将其转化为可执行的硬件电路&amp;#xff0c;需要进行综合、布局和路由等步骤。&lt;/p&gt;

&lt;p&gt;参考GPT和自己的思路&amp;#xff1a;要确定PWM高电平的时间&amp;#xff0c;需要根据电机的转速、控制方式以及所需的输出功率来计算得出。对于控制电机的PWM信号&amp;#xff0c;可以考虑使用三相交错PWM信号来控制。这种方式可以通过将三个PWM信号相互错开120度的方式&amp;#xff0c;来控制电机的转速和方向。&lt;/p&gt;
&lt;p&gt;在使用逆变电路将PWM信号转换为三相交流电源时&amp;#xff0c;需要考虑到逆变电路的效率和输出功率。逆变电路的输出功率可以通过控制PWM信号的高电平时间和占空比来实现。一般来说&amp;#xff0c;PWM信号的高电平时间越长&amp;#xff0c;输出功率越大&amp;#xff0c;但是过长的高电平时间可能会导致逆变电路的效率下降或者损坏电机。&lt;/p&gt;
&lt;p&gt;因此&amp;#xff0c;在确定PWM高电平的时间时&amp;#xff0c;需要综合考虑输出功率、逆变电路的效率和电机的保护等因素。对于初学者来说&amp;#xff0c;可以参考一些电机控制方案&amp;#xff0c;比如开环控制或者闭环控制方案&amp;#xff0c;来确定PWM信号的高电平时间。同时&amp;#xff0c;也可以通过实验来测试不同高电平时间下的电机转速和输出功率&amp;#xff0c;从而确定最佳的PWM控制方案。&lt;br /&gt;控制三相电机的整个流程一般如下&amp;#xff1a;&lt;/p&gt;
&lt;p&gt;1  设计逆变器电路&amp;#xff1a;根据电机的参数&amp;#xff08;例如电压、电流、功率等&amp;#xff09;以及所需的电机驱动方式&amp;#xff08;例如直接逆变、三电平逆变等&amp;#xff09;&amp;#xff0c;设计逆变器电路。&lt;/p&gt;
&lt;p&gt;2  编写控制算法&amp;#xff1a;根据电机的工作原理和控制要求&amp;#xff0c;设计电机控制算法。可以使用经典的控制算法&amp;#xff08;例如PID控制&amp;#xff09;&amp;#xff0c;也可以使用先进的控制算法&amp;#xff08;例如模型预测控制&amp;#xff09;。&lt;/p&gt;
&lt;p&gt;3  编写PWM输出程序&amp;#xff1a;使用DSP芯片控制PWM输出。根据电机控制算法的要求&amp;#xff0c;编写PWM输出程序&amp;#xff0c;并将其烧录到DSP芯片中。&lt;/p&gt;
&lt;p&gt;4  调试和优化&amp;#xff1a;将电机连接到逆变器电路上&amp;#xff0c;运行控制算法和PWM输出程序&amp;#xff0c;并对电机进行调试和优化&amp;#xff0c;以达到预期的控制效果。&lt;/p&gt;
&lt;p&gt;需要注意的是&amp;#xff0c;在整个流程中需要考虑到安全性、稳定性、可靠性等因素&amp;#xff0c;尤其是电机驱动部分&amp;#xff0c;需要特别谨慎&amp;#xff0c;避免损坏电机或逆变器电路。同时&amp;#xff0c;还需要根据具体的应用场景和需求&amp;#xff0c;进行参数调整和优化。&lt;/p&gt;

&lt;p&gt;该回答引用GPTᴼᴾᴱᴺᴬᴵ&lt;br /&gt;以下是基于 VHDL 语言实现八路彩灯的仿真代码&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-attribute"&gt;library&lt;/span&gt; IEEE;
&lt;span class="hljs-attribute"&gt;use&lt;/span&gt; IEEE.STD_LOGIC_1164.&lt;span class="hljs-literal"&gt;ALL&lt;/span&gt;;
&lt;span class="hljs-attribute"&gt;use&lt;/span&gt; IEEE.STD_LOGIC_ARITH.&lt;span class="hljs-literal"&gt;ALL&lt;/span&gt;;
&lt;span class="hljs-attribute"&gt;use&lt;/span&gt; IEEE.STD_LOGIC_UNSIGNED.&lt;span class="hljs-literal"&gt;ALL&lt;/span&gt;;

&lt;span class="hljs-attribute"&gt;entity&lt;/span&gt; eight_light is
    &lt;span class="hljs-attribute"&gt;Port&lt;/span&gt; ( clk : in STD_LOGIC;
           &lt;span class="hljs-attribute"&gt;rst&lt;/span&gt; : in STD_LOGIC;
           &lt;span class="hljs-attribute"&gt;light&lt;/span&gt; : out STD_LOGIC_VECTOR(&lt;span class="hljs-number"&gt;7&lt;/span&gt; downto &lt;span class="hljs-number"&gt;0&lt;/span&gt;));
&lt;span class="hljs-attribute"&gt;end&lt;/span&gt; eight_light;

&lt;span class="hljs-attribute"&gt;architecture&lt;/span&gt; Behavioral of eight_light is

    &lt;span class="hljs-attribute"&gt;signal&lt;/span&gt; count : integer range &lt;span class="hljs-number"&gt;0&lt;/span&gt; to &lt;span class="hljs-number"&gt;2000&lt;/span&gt; :&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-attribute"&gt;signal&lt;/span&gt; pattern : integer range &lt;span class="hljs-number"&gt;1&lt;/span&gt; to &lt;span class="hljs-number"&gt;4&lt;/span&gt; :&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    &lt;span class="hljs-attribute"&gt;signal&lt;/span&gt; left_light, right_light : integer range &lt;span class="hljs-number"&gt;0&lt;/span&gt; to &lt;span class="hljs-number"&gt;7&lt;/span&gt; :&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-attribute"&gt;signal&lt;/span&gt; dir : integer range -&lt;span class="hljs-number"&gt;1&lt;/span&gt; to &lt;span class="hljs-number"&gt;1&lt;/span&gt; :&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    &lt;span class="hljs-attribute"&gt;signal&lt;/span&gt; shift : integer range &lt;span class="hljs-number"&gt;0&lt;/span&gt; to &lt;span class="hljs-number"&gt;3&lt;/span&gt; :&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-attribute"&gt;signal&lt;/span&gt; timer : integer range &lt;span class="hljs-number"&gt;0&lt;/span&gt; to &lt;span class="hljs-number"&gt;2000&lt;/span&gt; :&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-attribute"&gt;signal&lt;/span&gt; light_status : STD_LOGIC_VECTOR(&lt;span class="hljs-number"&gt;7&lt;/span&gt; downto &lt;span class="hljs-number"&gt;0&lt;/span&gt;) :&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;11111111&amp;#34;&lt;/span&gt;;

&lt;span class="hljs-attribute"&gt;begin&lt;/span&gt;

    &lt;span class="hljs-attribute"&gt;process&lt;/span&gt;(clk, rst)
    &lt;span class="hljs-attribute"&gt;begin&lt;/span&gt;
        &lt;span class="hljs-attribute"&gt;if&lt;/span&gt; rst &amp;#61; &amp;#39;&lt;span class="hljs-number"&gt;1&lt;/span&gt;&amp;#39; then
            &lt;span class="hljs-attribute"&gt;count&lt;/span&gt; &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-attribute"&gt;pattern&lt;/span&gt; &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
            &lt;span class="hljs-attribute"&gt;left_light&lt;/span&gt; &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-attribute"&gt;right_light&lt;/span&gt; &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;7&lt;/span&gt;;
            &lt;span class="hljs-attribute"&gt;dir&lt;/span&gt; &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
            &lt;span class="hljs-attribute"&gt;shift&lt;/span&gt; &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-attribute"&gt;timer&lt;/span&gt; &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-attribute"&gt;light_status&lt;/span&gt; &amp;lt;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;11111111&amp;#34;&lt;/span&gt;;
        &lt;span class="hljs-attribute"&gt;elsif&lt;/span&gt; rising_edge(clk) then
            &lt;span class="hljs-attribute"&gt;if&lt;/span&gt; count &amp;#61; &lt;span class="hljs-number"&gt;500&lt;/span&gt; then
                &lt;span class="hljs-attribute"&gt;count&lt;/span&gt; &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
                &lt;span class="hljs-attribute"&gt;case&lt;/span&gt; pattern is
                    &lt;span class="hljs-attribute"&gt;when&lt;/span&gt; &lt;span class="hljs-number"&gt;1&lt;/span&gt; &amp;#61;&amp;gt;
                        &lt;span class="hljs-attribute"&gt;if&lt;/span&gt; shift &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; then
                            &lt;span class="hljs-attribute"&gt;light_status&lt;/span&gt; &amp;lt;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;11111111&amp;#34;&lt;/span&gt;;
                            &lt;span class="hljs-attribute"&gt;if&lt;/span&gt; left_light &amp;#61; &lt;span class="hljs-number"&gt;7&lt;/span&gt; and right_light &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; then
                                &lt;span class="hljs-attribute"&gt;pattern&lt;/span&gt; &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;2&lt;/span&gt;;
                            &lt;span class="hljs-attribute"&gt;else&lt;/span&gt;
                                &lt;span class="hljs-attribute"&gt;if&lt;/span&gt; dir &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt; then
                                    &lt;span class="hljs-attribute"&gt;left_light&lt;/span&gt; &amp;lt;&amp;#61; left_light &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                                    &lt;span class="hljs-attribute"&gt;right_light&lt;/span&gt; &amp;lt;&amp;#61; right_light - &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                                    &lt;span class="hljs-attribute"&gt;if&lt;/span&gt; left_light &amp;#61; right_light then
                                        &lt;span class="hljs-attribute"&gt;dir&lt;/span&gt; &amp;lt;&amp;#61; -&lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                                    &lt;span class="hljs-attribute"&gt;end&lt;/span&gt; if;
                                &lt;span class="hljs-attribute"&gt;else&lt;/span&gt;
                                    &lt;span class="hljs-attribute"&gt;left_light&lt;/span&gt; &amp;lt;&amp;#61; left_light - &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                                    &lt;span class="hljs-attribute"&gt;right_light&lt;/span&gt; &amp;lt;&amp;#61; right_light &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
