    &lt;span class="hljs-type"&gt;int&lt;/span&gt; B[COL_A][COL_B];  &lt;span class="hljs-comment"&gt;// 定义 4x2 矩阵 B&lt;/span&gt;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; C[ROW_A][COL_B];  &lt;span class="hljs-comment"&gt;// 定义 3x2 矩阵 C&lt;/span&gt;

