    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; ROW_A; i&amp;#43;&amp;#43;) {
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; COL_A; j&amp;#43;&amp;#43;) {
