&lt;pre&gt;&lt;code class="language-c&amp;#43;&amp;#43;"&gt;#include &amp;lt;stdio.h&amp;gt;
#define &lt;span class="hljs-symbol"&gt;N&lt;/span&gt; &lt;span class="hljs-number"&gt;20&lt;/span&gt;
int is_symmetricmatrix(int a[][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;], int &lt;span class="hljs-symbol"&gt;Row&lt;/span&gt;,int &lt;span class="hljs-symbol"&gt;Col&lt;/span&gt;)//判断一个&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;*&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;矩阵&amp;#xff0c;是否是对称矩阵&amp;#xff0c;是返回&lt;span class="hljs-number"&gt;1&lt;/span&gt;&amp;#xff0c;否返回&lt;span class="hljs-number"&gt;0&lt;/span&gt;
{
    int i, j, flag;
    for (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;, flag &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-symbol"&gt;Row&lt;/span&gt;; i&amp;#43;&amp;#43;)
    {
        for (j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; i; j&amp;#43;&amp;#43;)
        {
            if (a[i][j] !&amp;#61; a[j][i])
            {
                flag &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
                break;
            }
        }
        if (j &amp;lt; i) break;
    }
    if (flag)
        return &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    else
        return &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}
void getTransposeMatrix(int b[][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;], int a[][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;], int &lt;span class="hljs-symbol"&gt;Row&lt;/span&gt;, int &lt;span class="hljs-symbol"&gt;Col&lt;/span&gt;)//矩阵转置
{
    int i, j;
    for (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-symbol"&gt;Row&lt;/span&gt;; i&amp;#43;&amp;#43;)
        for (j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; &lt;span class="hljs-symbol"&gt;Col&lt;/span&gt;; j&amp;#43;&amp;#43;)
            b[j][i] &amp;#61; a[i][j];
}
void matrixMul(int a[][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;], int b[][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;], int c[][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;], int &lt;span class="hljs-symbol"&gt;Rowa&lt;/span&gt;, int &lt;span class="hljs-symbol"&gt;Cola&lt;/span&gt;, int &lt;span class="hljs-symbol"&gt;Colb&lt;/span&gt;)//矩阵相乘
{
    int i, j, k;
    for(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;&lt;span class="hljs-symbol"&gt;Rowa&lt;/span&gt;;i&amp;#43;&amp;#43;)
    {
        for (j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; &lt;span class="hljs-symbol"&gt;Colb&lt;/span&gt;; j&amp;#43;&amp;#43;)
        {
            for (k &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; k &amp;lt; &lt;span class="hljs-symbol"&gt;Cola&lt;/span&gt;; k&amp;#43;&amp;#43;)
                c[i][j] &amp;#43;&amp;#61; a[i][k] * b[k][j];
        }
    }
}
void matrixAdd(int a[][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;], int b[][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;], int c[][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;], int &lt;span class="hljs-symbol"&gt;Rowa&lt;/span&gt;, int &lt;span class="hljs-symbol"&gt;Cola&lt;/span&gt;)//矩阵相加
{
    int i, j;
    for (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-symbol"&gt;Rowa&lt;/span&gt;; i&amp;#43;&amp;#43;)
        for (j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; &lt;span class="hljs-symbol"&gt;Cola&lt;/span&gt;; j&amp;#43;&amp;#43;)
            c[i][j] &amp;#61; a[i][j] &amp;#43; b[i][j];
}
void printmatrix(int a[][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;], int &lt;span class="hljs-symbol"&gt;Row&lt;/span&gt;, int &lt;span class="hljs-symbol"&gt;Col&lt;/span&gt;)
{
    int i, j;
    for (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-symbol"&gt;Row&lt;/span&gt;; i&amp;#43;&amp;#43;){
        for (j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; &lt;span class="hljs-symbol"&gt;Col&lt;/span&gt;; j&amp;#43;&amp;#43;)
        {
            printf(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;, a[i][j]);
        }
        printf(&lt;span class="hljs-string"&gt;&amp;#34;\n&amp;#34;&lt;/span&gt;);
    }
}
int main()
{
    int a[&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;] &amp;#61; { &lt;span class="hljs-number"&gt;0&lt;/span&gt; }, b[&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;] &amp;#61; { &lt;span class="hljs-number"&gt;0&lt;/span&gt; }, c[&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;][&lt;span class="hljs-symbol"&gt;N&lt;/span&gt;] &amp;#61; {&lt;!-- --&gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;}, i, j, n &amp;#61; &lt;span class="hljs-number"&gt;3&lt;/span&gt;;
    scanf(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, &amp;amp;n);
    for (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; n; i&amp;#43;&amp;#43;)
        for (j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; n; j&amp;#43;&amp;#43;)
            scanf(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, &amp;amp;a[i][j]);
    if (is_symmetricmatrix(a, n, n))
    {
        printf(&lt;span class="hljs-string"&gt;&amp;#34;YES\n&amp;#34;&lt;/span&gt;);
        getTransposeMatrix(b, a, n, n);
        matrixMul(a, b, c, n, n, n);
        printmatrix(c, n, n);
    }
    else{
        printf(&lt;span class="hljs-string"&gt;&amp;#34;NO\n&amp;#34;&lt;/span&gt;);
        getTransposeMatrix(b, a, n, n);
        matrixAdd(a, b, c, n, n);
        printmatrix(c, n, n);
    }
    return &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

