                &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-variable"&gt;j&lt;/span&gt; &lt;span class="hljs-operator"&gt;&amp;#61;&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j&amp;lt; b.getCol();j&amp;#43;&amp;#43;)
