                    {
                        data[i][j]&amp;#61;data[i][j]&amp;#43;a.get(i, k)*b.get(k, j);&lt;span class="hljs-comment"&gt;//矩阵乘法运算&lt;/span&gt;
                    }

           d&amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-title class_"&gt;juzhen&lt;/span&gt;(data);&lt;span class="hljs-comment"&gt;//将矩阵给d&lt;/span&gt;
            &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; d;&lt;span class="hljs-comment"&gt;//返回矩阵d    &lt;/span&gt;
        }
    }

&lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title class_"&gt;test&lt;/span&gt; {
