&lt;p&gt;我引用ChatGPT作答&amp;#xff1a;&lt;br /&gt;大概这样呢&amp;#xff1f;不知道行不行。&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;
&lt;span class="hljs-attribute"&gt;lamd&lt;/span&gt;&amp;#61;0.193*10^-3; % 入射波长
&lt;span class="hljs-attribute"&gt;w&lt;/span&gt;&amp;#61;1.5; % 入射光束腰
&lt;span class="hljs-attribute"&gt;R&lt;/span&gt;&amp;#61;1.5; % 元件的极限孔径
&lt;span class="hljs-attribute"&gt;D&lt;/span&gt;&amp;#61;175*10^-3; % 衍射距离
&lt;span class="hljs-attribute"&gt;L0&lt;/span&gt;&amp;#61;10.5*10^-3; % 照明区域半径
&lt;span class="hljs-attribute"&gt;r1&lt;/span&gt;&amp;#61;7.5*10^-3; % 内半径
&lt;span class="hljs-attribute"&gt;r2&lt;/span&gt;&amp;#61;10.5*10^-3; % 外半径
%&lt;span class="hljs-attribute"&gt;theta&lt;/span&gt;&amp;#61;45*pi/180; % 极角
theta &amp;#61; pi/4;
&lt;span class="hljs-attribute"&gt;k&lt;/span&gt;&amp;#61;2*pi/lamd; % 波数
&lt;span class="hljs-attribute"&gt;N&lt;/span&gt;&amp;#61;256;


% 生成坐标网格
&lt;span class="hljs-attribute"&gt;x&lt;/span&gt;&amp;#61;linspace(-L0/2,L0/2,N);
&lt;span class="hljs-attribute"&gt;y&lt;/span&gt;&amp;#61;linspace(-L0/2,L0/2,N);
[X,Y]&amp;#61;meshgrid(x,y);

% 生成圆形光阑
&lt;span class="hljs-attribute"&gt;J1&lt;/span&gt;&amp;#61;zeros(N);
&lt;span class="hljs-keyword"&gt;for&lt;/span&gt; &lt;span class="hljs-attribute"&gt;m&lt;/span&gt;&amp;#61;1:N
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; &lt;span class="hljs-attribute"&gt;n&lt;/span&gt;&amp;#61;1:N
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; X(m,n)^2&amp;#43;Y(m,n)^2&amp;lt;&amp;#61;R^2
            J1(m,n)&amp;#61;1;
        end
    end
end

% 生成高斯光束
&lt;span class="hljs-attribute"&gt;A&lt;/span&gt;&amp;#61;exp(-(X.^2&amp;#43;Y.^2)/w^2).*J1;

% 生成偶极照明
&lt;span class="hljs-attribute"&gt;J4&lt;/span&gt;&amp;#61;zeros(N);
&lt;span class="hljs-keyword"&gt;for&lt;/span&gt; &lt;span class="hljs-attribute"&gt;m&lt;/span&gt;&amp;#61;1:N
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; &lt;span class="hljs-attribute"&gt;n&lt;/span&gt;&amp;#61;1:N
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; X(m,n)^2&amp;#43;Y(m,n)^2&amp;gt;&amp;#61;r1^2 &amp;amp;&amp;amp; X(m,n)^2&amp;#43;Y(m,n)^2&amp;lt;&amp;#61;r2^2 &amp;amp;&amp;amp; abs(atan2(Y(m,n),X(m,n)))&amp;lt;&amp;#61;theta/2
            J4(m,n)&amp;#61;1;
        end
    end
end

% 计算光阑和高斯光束的中心
J1_center &amp;#61; [round(N/2), round(N/2)];
A_center &amp;#61; [round(N/2), round(N/2)];

% 计算光阑和高斯光束需要平移的距离
J1_shift &amp;#61; J1_center - [&lt;span class="hljs-built_in"&gt;find&lt;/span&gt;(J1(:,1),1,&lt;span class="hljs-string"&gt;&amp;#39;first&amp;#39;&lt;/span&gt;), &lt;span class="hljs-built_in"&gt;find&lt;/span&gt;(J1(1,:),1,&lt;span class="hljs-string"&gt;&amp;#39;first&amp;#39;&lt;/span&gt;)];
A_shift &amp;#61; A_center - [&lt;span class="hljs-built_in"&gt;find&lt;/span&gt;(A(:,1),1,&lt;span class="hljs-string"&gt;&amp;#39;first&amp;#39;&lt;/span&gt;), &lt;span class="hljs-built_in"&gt;find&lt;/span&gt;(A(1,:),1,&lt;span class="hljs-string"&gt;&amp;#39;first&amp;#39;&lt;/span&gt;)];

% 平移光阑和高斯光束
J1 &amp;#61; circshift(J1, J1_shift);
A &amp;#61; circshift(A, A_shift);

% 生成扇形光阑
&lt;span class="hljs-keyword"&gt;for&lt;/span&gt; &lt;span class="hljs-attribute"&gt;m&lt;/span&gt;&amp;#61;1:N
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; &lt;span class="hljs-attribute"&gt;n&lt;/span&gt;&amp;#61;1:N
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; J4(m,n) &amp;amp;&amp;amp; abs(atan2(Y(m,n),X(m,n)))&amp;gt;theta/2
            J4(m,n)&amp;#61;0;
        end
    end
end

% 计算归一化系数
&lt;span class="hljs-attribute"&gt;a&lt;/span&gt;&amp;#61;sum(sum(A.^2))/sum(sum(J4.^2));
&lt;span class="hljs-attribute"&gt;J4&lt;/span&gt;&amp;#61;J4*sqrt(a);

% 变换到衍射面
&lt;span class="hljs-attribute"&gt;A_fft&lt;/span&gt;&amp;#61;fftshift(fft2(A));
&lt;span class="hljs-attribute"&gt;J4_fft&lt;/span&gt;&amp;#61;fftshift(fft2(J4));
&lt;span class="hljs-attribute"&gt;x_fft&lt;/span&gt;&amp;#61;linspace(-1/(2*D),1/(2*D),N);
[X_fft,Y_fft]&amp;#61;meshgrid(x_fft,x_fft);
&lt;span class="hljs-attribute"&gt;phase_term&lt;/span&gt;&amp;#61;exp(1i*k*(X_fft.^2&amp;#43;Y_fft.^2)*D);
&lt;span class="hljs-attribute"&gt;B_fft&lt;/span&gt;&amp;#61;J4_fft.*phase_term.*A_fft;

% 变换回物面
&lt;span class="hljs-attribute"&gt;B&lt;/span&gt;&amp;#61;ifft2(ifftshift(B_fft));

% 显示照明光斑
subplot(1,3,1);
imagesc(x,y,J4); axis square;
title(&lt;span class="hljs-string"&gt;&amp;#39;Illumination Pattern&amp;#39;&lt;/span&gt;)
xlabel(&lt;span class="hljs-string"&gt;&amp;#39;x / mm&amp;#39;&lt;/span&gt;); ylabel(&lt;span class="hljs-string"&gt;&amp;#39;y / mm&amp;#39;&lt;/span&gt;);


% 显示高斯光束
subplot(1,3,2);
imagesc(x11,y11,A);axis square;
title(&lt;span class="hljs-string"&gt;&amp;#39;Gaussian Beam&amp;#39;&lt;/span&gt;)
xlabel(&lt;span class="hljs-string"&gt;&amp;#39;x / mm&amp;#39;&lt;/span&gt;);ylabel(&lt;span class="hljs-string"&gt;&amp;#39;y / mm&amp;#39;&lt;/span&gt;);
% 显示衍射图样
subplot(1,3,3);
imagesc(x,y,abs(B).^2); axis square;
title(&lt;span class="hljs-string"&gt;&amp;#39;Diffraction Pattern&amp;#39;&lt;/span&gt;)
xlabel(&lt;span class="hljs-string"&gt;&amp;#39;x / mm&amp;#39;&lt;/span&gt;); ylabel(&lt;span class="hljs-string"&gt;&amp;#39;y / mm&amp;#39;&lt;/span&gt;);

% 生成偶极照明和高斯光束的交叉项
J5 &amp;#61; zeros(N);
&lt;span class="hljs-keyword"&gt;for&lt;/span&gt; &lt;span class="hljs-attribute"&gt;m&lt;/span&gt;&amp;#61;1:N
&lt;span class="hljs-keyword"&gt;for&lt;/span&gt; &lt;span class="hljs-attribute"&gt;n&lt;/span&gt;&amp;#61;1:N
&lt;span class="hljs-keyword"&gt;if&lt;/span&gt; ~isnan(J2(m,n)) &amp;amp;&amp;amp; ~isnan(A(m,n)) &amp;amp;&amp;amp; J2(m,n) &amp;amp;&amp;amp; A(m,n)
J5(m,n) &amp;#61; 1;
end
end
end

% 显示交叉项
subplot(1,3,3);
imagesc(x11,y11,J5);axis square;
title(&lt;span class="hljs-string"&gt;&amp;#39;Cross Term&amp;#39;&lt;/span&gt;)
xlabel(&lt;span class="hljs-string"&gt;&amp;#39;x / mm&amp;#39;&lt;/span&gt;);ylabel(&lt;span class="hljs-string"&gt;&amp;#39;y / mm&amp;#39;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;
