package demo;

import java.util.HashMap;
import java.util.Map;

/**
 * @author yeyong
 */
public class Test24 {

  public static void main(String args[]) {
    int a = 100;
    int b = 200;
    int c = 300;

    String A = "a+b"; // 300
    String B = "A+b"; // 500
    String C = "A+B"; // 800
    String D = "C+A+b"; // 1300

    Map&lt;String, Object&gt; context = new HashMap&lt;String, Object&gt;();
    context.put("a", a);
    context.put("b", b);
    context.put("c", c);
    context.put("A", A);
    context.put("B", B);
    context.put("C", C);
    context.put("D", D);

    System.out.println("D = " + getVarValue(context, "D"));
    System.out.println("data : " + context);
  }

  private static int getVarValue(Map&lt;String, Object&gt; data, String var) {
    Object o = data.get(var);
    if (o instanceof Integer) {
      return (Integer) o;
    } else if (o instanceof String) {
      String exp = (String) o;
      int sum = 0;
      for (String v : exp.split("\\s*\\+\\s*")) {
        int value = getVarValue(data, v);
        sum += value;
      }
      data.put(var, sum);
      return sum;
    }
    throw new RuntimeException("ERROR");
  }
}

