    private List&lt;Integer&gt; findNumber(List&lt;Integer&gt; sourceList,
            List&lt;Integer&gt; targetList)
    {
        List&lt;Integer&gt; resultList = new ArrayList&lt;Integer&gt;();
        for (Integer i : sourceList)
