struct Results solution(int T[],int M)
{
	int cap = 0;
	int* res = (int*)malloc((M-1)*sizeof(int));
	for(int i=0;i&lt;M-1;i++)
	{
		res[i] = 0;
	}
	for(int i=0;i&lt;M;i++)
	{
		int v = T[i];
		int n = i;

		int index = 0;
		if(n == v)
		{
			continue;
		}
		while(v != n)
		{
			index++;
			n = v;
			v = T[v];
		}

		res[index-1]++;
	}

	Results* rs = (Results *)malloc(sizeof(Results));
	rs-&gt;A = res;
	rs-&gt;N = M-1;

	return *rs;
}
