class FirstUnique 
{
public:
    unordered_map<int,int> num_freq;
    vector<int> rec;

    FirstUnique(vector<int>& nums) 
    {
        for (int x: nums)
            num_freq[x] ++;
        for (int x : nums)
            if (num_freq[x] == 1)
                rec.push_back(x);
    }

    int showFirstUnique() 
    {
        int n = rec.size();
        int res = -1;
        int i = 0;
        while (i < n)
        {
            if (num_freq[rec[i]] == 1)
            {
                res = rec[i];
                rec.erase(rec.begin(), rec.begin() + i);
                break;
            }
            i ++;
        }
        return res;

    }

    void add(int value) 
    {
        num_freq[value] ++;
        if (num_freq[value] == 1)
        {
            rec.push_back(value);
        }
    }
};

