                    }
                    n=s[0]*100+s[1]*10+s[2];   
                    if(n==2*(i*100+j*10+k)) { System.out.println(i+""+j+""+k+"    "+n+"    "+m);}
                    n=s[0]*100+s[2]*10+s[1];    
                    if(n==2*(i*100+j*10+k)) { System.out.println(i+""+j+""+k+"    "+n+"    "+m);}
                    n=s[1]*100+s[0]*10+s[2];   
                    if(n==2*(i*100+j*10+k)) { System.out.println(i+""+j+""+k+"    "+n+"    "+m);}
                    n=s[1]*100+s[2]*10+s[0]; 
                    if(n==2*(i*100+j*10+k)) { System.out.println(i+""+j+""+k+"    "+n+"    "+m);}
                    n=s[2]*100+s[0]*10+s[1]; 
                    if(n==2*(i*100+j*10+k)) { System.out.println(i+""+j+""+k+"    "+n+"    "+m);}
                    n=s[2]*100+s[1]*10+s[0]; 
                    if(n==2*(i*100+j*10+k)) { System.out.println(i+""+j+""+k+"    "+n+"    "+m);}
                    set.remove(t1);
                    set.remove(t2);
                    set.remove(t3);
                    set.remove(k);
				} 
				   set.remove(j);
			}
			set.remove(i);
		}
	}
