void bubbleSort()//冒泡排序
{
    int i,j,k;
    printf("原始数据为：\n");
    for(i=0;i<LENGTH;i++)
    {
        printf("%5d",A[i]);
    }
    printf("\n");

    for(i=0;i<LENGTH;i++)
    {
        for(j=0;j<LENGTH-1;j++)
        {
            if(A[j]>A[j+1])
            {
                k=A[j];
                A[j]=A[j+1];
                A[j+1]=k;
            }
        }
    }
    printf("排序后结果为：\n");
    for(i=0;i<LENGTH;i++)
    {
        printf("%5d",A[i]);
    }
    printf("\n");
}
void quickSort(int low,int high)//快速排序
{
    int first=low;
    int last=high;
    int key=A[first];
    if(low>=high)
        return;
    while(first<last)
    {
        while(first<last&&A[last]>key)
        {
            last--;
        }
        A[first]=A[last];

        while(first<last&&A[first]<key)
        {
            first++;
        }
        A[last]=A[first];
    }
    A[first]=key;

    quickSort(low,first-1);
    quickSort(first+1,high);

}
void insertSort()//插入排序
{
    int i,j,k;
    printf("原始数据为：\n");
    for(i=0;i<LENGTH;i++)
    {
        printf("%5d",A[i]);
    }
    printf("\n");

    for(i=1;i<LENGTH;i++)
    {
        k=A[i];
        for(j=i-1;j>=0&&A[j]>k;j--)
        {
            A[j+1]=A[j];
        }
        A[j+1]=k;
    }

    printf("排序后结果为：\n");
    for(i=0;i<LENGTH;i++)
    {
        printf("%5d",A[i]);
    }
    printf("\n");
}
void shellSort()//希尔排序
{
    int i,j,k,gap,tmp;
    printf("原始数据为：\n");
    for(i=0;i<LENGTH;i++)
    {
        printf("%5d",A[i]);
    }
    printf("\n");

    for(gap=LENGTH/2;gap>0;gap/=2)// 步长初始化为数组长度的一半
    {
        for (i=0;i<gap;++i)// 变量 i 为每次分组的第一个元素下标
        {
            for (j=i+gap;j<LENGTH;j+=gap)//对步长为gap的元素进行直插排序，当gap为1时，就是直插排序
            {
	            tmp=A[j];  // 备份A[j]的值
	            k=j-gap;  // j初始化为i的前一个元素（与i相差gap长度）
	            while(k>=0&&A[k]>tmp)
	            {
	                A[k+gap]=A[k]; // 将在A[i]前且比tmp的值大的元素向后移动一位
	                k-=gap;
	            }
	            A[k+gap]=tmp;
	        }
	    }
    }

    printf("排序后结果为：\n");
    for(i=0;i<LENGTH;i++)
    {
        printf("%5d",A[i]);
    }
    printf("\n");
}

