#include"iostream"
using namespace std;

int Temp(int n){//求-1的n次方
    if(n==0)return 1;
    return -1*Temp(n-1);
}

double PI(){
    double sum=0;
    double temp=1.0;
    int count=0;//计数变量
    while(temp>=1e-6){
        count++;
        sum=sum+Temp(count-1)*temp;
        temp=1.0/(2*count+1);
    }
    return 4*sum;
}

