			scanf("%c",&amp;help);
			if(help=='Y')//Y选择帮助
			{
				int next,next1;
				int num1,num2;
				printf("Select help mode ：position:1/Correct:2");//选择帮助类型，1为查看对应位置正确性，2为查看正确数字
				scanf("%d",&amp;j);
				if(j==1)//1为查看对应位置正确性
				{
					num1=in;num2=r;
					char s[4];
					for(next=0;next&lt;4;next++)
					{
						if(num1%10==num2%10)//对应位置对比
						{
							s[4-next-1]="R";
						}
						else
						{
							s[4-next-1]="E";
						}
						num1=num1/10;
						num2=num2/10;
					}
