&lt;p&gt;时间复杂度为O(n4),一般超过O(n3)算法上就存在问题。&lt;br /&gt;&lt;strong&gt;参考&amp;#xff1a;&lt;/strong&gt;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-class"&gt;&lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title"&gt;UnionFind&lt;/span&gt;:&lt;/span&gt;
    def __init__(&lt;span class="hljs-built_in"&gt;self&lt;/span&gt;, n: int):
        &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.parent &amp;#61; list(range(n))
        &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.rank &amp;#61; [&lt;span class="hljs-number"&gt;0&lt;/span&gt;] * n

    def find(&lt;span class="hljs-built_in"&gt;self&lt;/span&gt;, x: int) -&amp;gt; int:
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.parent[x] !&amp;#61; x:
            &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.parent[x] &amp;#61; &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.find(&lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.parent[x])
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.parent[x]

    def merge(&lt;span class="hljs-built_in"&gt;self&lt;/span&gt;, x: int, y: int) -&amp;gt; None:
        x, y &amp;#61; &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.find(x), &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.find(y)
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; x &amp;#61;&amp;#61; y:
            &lt;span class="hljs-keyword"&gt;return&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.rank[x] &amp;gt; &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.rank[y]:
            &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.parent[y] &amp;#61; x
        elif &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.rank[x] &amp;lt; &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.rank[y]:
            &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.parent[x] &amp;#61; y
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;:
            &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.parent[y] &amp;#61; x
            &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.rank[x] &amp;#43;&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;

&lt;span class="hljs-class"&gt;&lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title"&gt;Solution&lt;/span&gt;:&lt;/span&gt;
    def largestComponentSize(&lt;span class="hljs-built_in"&gt;self&lt;/span&gt;, nums: List[int]) -&amp;gt; int:
        uf &amp;#61; UnionFind(&lt;span class="hljs-built_in"&gt;max&lt;/span&gt;(nums) &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;)
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; num in nums:
            i &amp;#61; &lt;span class="hljs-number"&gt;2&lt;/span&gt;
            &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; i * i &amp;lt;&amp;#61; num:
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; num % i &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;:
                    uf.merge(num, i)
                    uf.merge(num, num // i)
                i &amp;#43;&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-built_in"&gt;max&lt;/span&gt;(Counter(uf.find(num) &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; num in nums).values())
&lt;/code&gt;&lt;/pre&gt;

