```
#include "stdio.h"

void foo(int * arr, int n, int acc)
{
	if (acc == n * n) return;
	if (acc / n &gt; acc % n)
		printf("%d ", arr[acc / n] + arr[acc % n]);
	foo(arr, n, acc + 1);
}

int main()
{
	int arr[] = { 7, 5, 3, 4 };
	foo(arr, 4, 0);
	return 0;
}
