 void Link::Swap(node *p,node *t)
{
    node *temp;
    if(t->next==NULL&&p->prior!=NULL) //t结点是否为尾结点
    {
        if(p->next==t) //p,t结点是否相邻
        {
            //与尾结点相邻的交换代
            t->next=p;
            t->prior=p->prior;
            p->next=NULL;
            p->prior->next=t;
           p->prior=t;
        }
       else
        {
            //与尾结点不相邻的交换代
            t->next=p->next;
            t->prior->next=p;
            temp=t->prior;
            t->prior=p->prior;
            p->next->prior=t;
            p->next=NULL;
            p->prior->next=t;
            p->prior=temp;
        }
    }
    else if(p->prior==NULL&&t->next!=NULL)
    {
        if(p->next==t)
        {
            p->next=t->next;
            p->next->prior=p;
            p->prior=t;
            t->next=p;
            t->prior=NULL;
        }
        else
        {
            temp=p->next;
            p->next=t->next;
            t->next->prior=p;
            p->prior=t->prior;
            t->prior->next=p;
            t->next=temp;
            temp->prior=t;
            t->prior=NULL;
        }
    }
    else if(p->prior==NULL&&t->next==NULL)
    {
        p->next->prior=t;
        t->next=p->next;
        p->next=NULL;
        t->prior->next=p;
        p->prior=t->prior;
        t->prior=NULL;
    }
    else{
        if(p->next==t) //p,t结点是否相邻
        {
            //相邻的交换代
            t->next->prior=p;
