#include &lt;stdio.h&gt;

int main()
{
	int min = (1 &lt;&lt; 31) - 1;
	int idx = 0;
	int arr[] = {915,941,960,976,992,1015,1034,1050,1073,1089,1115,1131,1150,1166,1182,1208,1227};
	int n = 1000;
	for (int i = 0; i &lt; sizeof(arr) / sizeof(int); i++)
	{
		int diff = arr[i] - n;
		if (diff &lt; 0) diff = -diff;
		if (diff &lt; min)
		{
			min = diff;
			idx = i;
		}
	}
	printf("最接近的是%d 下标是%d", arr[idx], idx);
   	return 0;
}
