&lt;p&gt;傻子做法&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-c&amp;#43;&amp;#43;"&gt;
&lt;span class="hljs-meta"&gt;#include &amp;lt;bits/stdc&amp;#43;&amp;#43;.h&amp;gt;&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; namespace std;

const &lt;span class="hljs-type"&gt;int&lt;/span&gt; N&amp;#61;&lt;span class="hljs-number"&gt;1e6&lt;/span&gt;&amp;#43;&lt;span class="hljs-number"&gt;10&lt;/span&gt;;
&lt;span class="hljs-type"&gt;int&lt;/span&gt; treee[N];
&lt;span class="hljs-type"&gt;int&lt;/span&gt; sum&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;,flag&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;

&lt;span class="hljs-type"&gt;int&lt;/span&gt; find_public_father(&lt;span class="hljs-type"&gt;int&lt;/span&gt; u,&lt;span class="hljs-type"&gt;int&lt;/span&gt; v){
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; t1&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;,t2&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;,arr1[&lt;span class="hljs-number"&gt;10000&lt;/span&gt;],arr2[&lt;span class="hljs-number"&gt;10000&lt;/span&gt;];
    arr1[&lt;span class="hljs-number"&gt;0&lt;/span&gt;]&amp;#61;u; arr2[&lt;span class="hljs-number"&gt;0&lt;/span&gt;]&amp;#61;v;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(u&amp;gt;&lt;span class="hljs-number"&gt;1&lt;/span&gt;){
        u/&amp;#61;&lt;span class="hljs-number"&gt;2&lt;/span&gt;;
        arr1[t1&amp;#43;&amp;#43;]&amp;#61;u;
    }
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(v&amp;gt;&lt;span class="hljs-number"&gt;1&lt;/span&gt;){
        v/&amp;#61;&lt;span class="hljs-number"&gt;2&lt;/span&gt;;
        arr2[t2&amp;#43;&amp;#43;]&amp;#61;v;
    }
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;t1;i&amp;#43;&amp;#43;){
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;t2;j&amp;#43;&amp;#43;){
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(arr1[i]&amp;#61;&amp;#61;arr2[j]){
                &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; arr1[i];
            }
        }
    }
}

&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-keyword"&gt;add&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; u,&lt;span class="hljs-type"&gt;int&lt;/span&gt; v,&lt;span class="hljs-type"&gt;int&lt;/span&gt; w){
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(u&amp;#61;&amp;#61;v){
        flag&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt;;
    }&lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(u&amp;gt;v){
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt;;
    }
    &lt;span class="hljs-keyword"&gt;add&lt;/span&gt;(&lt;span class="hljs-number"&gt;2&lt;/span&gt;*u,v,w);
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(flag){
        treee[&lt;span class="hljs-number"&gt;2&lt;/span&gt;*u]&amp;#43;&amp;#61;w;
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt;;
    }
    &lt;span class="hljs-keyword"&gt;add&lt;/span&gt;(&lt;span class="hljs-number"&gt;2&lt;/span&gt;*u&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;,v,w);
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(flag){
        treee[&lt;span class="hljs-number"&gt;2&lt;/span&gt;*u&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;]&amp;#43;&amp;#61;w;
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt;;
    }
}

&lt;span class="hljs-type"&gt;void&lt;/span&gt; travel(&lt;span class="hljs-type"&gt;int&lt;/span&gt; u,&lt;span class="hljs-type"&gt;int&lt;/span&gt; v){
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(u&amp;#61;&amp;#61;v){
        flag&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt;;
    }&lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(u&amp;gt;v){
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt;;
    }
    travel(&lt;span class="hljs-number"&gt;2&lt;/span&gt;*u,v);
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(flag){
        sum&amp;#43;&amp;#61;treee[&lt;span class="hljs-number"&gt;2&lt;/span&gt;*u];
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt;;
    }
    travel(&lt;span class="hljs-number"&gt;2&lt;/span&gt;*u&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;,v);
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(flag){
        sum&amp;#43;&amp;#61;treee[&lt;span class="hljs-number"&gt;2&lt;/span&gt;*u&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;];
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt;;
