&lt;p&gt;&lt;/p&gt;&lt;p class="md_img_container"&gt;&lt;img align="left" alt="img" src="https://img-mid.csdnimg.cn/release/static/image/mid/ask/594377156846190.png" class="md_img" /&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-py"&gt;
&lt;span class="hljs-comment"&gt;# Python program for Kruskal&amp;#39;s algorithm to find&lt;/span&gt;
&lt;span class="hljs-comment"&gt;# Minimum Spanning Tree of a given connected,&lt;/span&gt;
&lt;span class="hljs-comment"&gt;# undirected and weighted graph&lt;/span&gt;


&lt;span class="hljs-comment"&gt;# Class to represent a graph&lt;/span&gt;


&lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title class_"&gt;Graph&lt;/span&gt;:

    &lt;span class="hljs-keyword"&gt;def&lt;/span&gt; &lt;span class="hljs-title function_"&gt;__init__&lt;/span&gt;(&lt;span class="hljs-params"&gt;self, vertices&lt;/span&gt;):
        self.V &amp;#61; vertices  &lt;span class="hljs-comment"&gt;# No. of vertices&lt;/span&gt;
        self.graph &amp;#61; []  &lt;span class="hljs-comment"&gt;# default dictionary&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;# to store graph&lt;/span&gt;

    &lt;span class="hljs-comment"&gt;# function to add an edge to graph&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;def&lt;/span&gt; &lt;span class="hljs-title function_"&gt;addEdge&lt;/span&gt;(&lt;span class="hljs-params"&gt;self, u, v, w&lt;/span&gt;):
        self.graph.append([u, v, w])

    &lt;span class="hljs-comment"&gt;# A utility function to find set of an element i&lt;/span&gt;
    &lt;span class="hljs-comment"&gt;# (uses path compression technique)&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;def&lt;/span&gt; &lt;span class="hljs-title function_"&gt;find&lt;/span&gt;(&lt;span class="hljs-params"&gt;self, parent, i&lt;/span&gt;):
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; parent[i] &amp;#61;&amp;#61; i:
            &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; i
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; self.find(parent, parent[i])

    &lt;span class="hljs-comment"&gt;# A function that does union of two sets of x and y&lt;/span&gt;
    &lt;span class="hljs-comment"&gt;# (uses union by rank)&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;def&lt;/span&gt; &lt;span class="hljs-title function_"&gt;union&lt;/span&gt;(&lt;span class="hljs-params"&gt;self, parent, rank, x, y&lt;/span&gt;):
        xroot &amp;#61; self.find(parent, x)
        yroot &amp;#61; self.find(parent, y)

        &lt;span class="hljs-comment"&gt;# Attach smaller rank tree under root of&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;# high rank tree (Union by Rank)&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; rank[xroot] &amp;lt; rank[yroot]:
            parent[xroot] &amp;#61; yroot
        &lt;span class="hljs-keyword"&gt;elif&lt;/span&gt; rank[xroot] &amp;gt; rank[yroot]:
            parent[yroot] &amp;#61; xroot

        &lt;span class="hljs-comment"&gt;# If ranks are same, then make one as root&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;# and increment its rank by one&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;:
            parent[yroot] &amp;#61; xroot
            rank[xroot] &amp;#43;&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;

    &lt;span class="hljs-comment"&gt;# The main function to construct MST using Kruskal&amp;#39;s&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;# algorithm&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;def&lt;/span&gt; &lt;span class="hljs-title function_"&gt;KruskalMST&lt;/span&gt;(&lt;span class="hljs-params"&gt;self&lt;/span&gt;):

        result &amp;#61; []  &lt;span class="hljs-comment"&gt;# This will store the resultant MST&lt;/span&gt;

        &lt;span class="hljs-comment"&gt;# An index variable, used for sorted edges&lt;/span&gt;
        i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;

        &lt;span class="hljs-comment"&gt;# An index variable, used for result[]&lt;/span&gt;
        e &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;

        &lt;span class="hljs-comment"&gt;# Step 1: Sort all the edges in&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;# non-decreasing order of their&lt;/span&gt;
