            // 取公钥对象
            PublicKey key &amp;#61; factory.generatePublic(keySpec);
            // 用公钥验证数字签名
            Signature signature &amp;#61; Signature.getInstance(KEY_RSA_SIGNATURE);
            signature.initVerify(key);
            signature.update(data);
            flag &amp;#61; signature.verify(decryptBase64(sign));
        } catch (Exception e)
        {
            e.printStackTrace();
        }
        return flag;
    }
}

