        if(ch>='0'&&ch<='9')//不是操作符，是操作数，进OVS栈 
        {
            int temp;
            temp=ch-'0';//先把当前操作数从字符变为数字 
            ch=getchar();
            while(ch>='0'&&ch<='9')//继续判断下一位是否为操作数
            {
                temp=temp*10+ch-'0';
                ch=getchar();
            } 
            Push(&OVS,temp);

        }
        else
        switch(Compare(ch,GetTop(&OPTR,&x)))
        {
            case '>':Push(&OPTR,ch);
