            //此处需要将v转换为字符型并且入站 
            Push(&OVS,v);
            break; 
        }
    }
    v=GetTop(&OVS,&x);
    return(v);
} 

//初始化
void InitStack(SeqStack *S)
{
    /*构造一个空栈S*/
    S->top=-1; 
} 
//进栈
int Push(SeqStack *S,StackElementType x)
{
    if(S->top==Stack_Size-1)
     return(FALSE);
    S->top++;
    S->elem[S->top]=x;
    return(TRUE);
} 
//出栈
int Pop(SeqStack *S,StackElementType *x)
{
    if(S->top==-1)
     return(FALSE);
    *x=S->elem[S->top];
    S->top--;
    return(TRUE);
} 
//读栈顶
int GetTop(SeqStack *S,StackElementType *x)
{
    if(S->top==-1)
     return(FALSE);
    else
    {
        *x=S->elem[S->top];
        return(TRUE);
    }
} 


//比较优先级，返回'>','<','=' 
StackElementType Compare(StackElementType ch1,StackElementType ch2)
//ch1为还未进栈的运算符，ch2为当前运算符栈顶元素 
{
    //'('未入栈时，优先级最高，入栈后，优先级最低 
    switch(ch1)
    {
        case '(':ch1=6;break;
        case '*':
        case '/':ch1=5;break;
        case '+':
        case '-':ch1=4;break;
        case ')':ch1=3;break;
    }
    switch(ch2)
    {
        case '*':
        case '/':ch2=5;break;
        case '+':
        case '-':ch2=4;break;
        case ')':ch2=3;break;
        case '(':ch2=2;break;
    }
    if(ch1>ch2)
     return('>');
     else if(ch1=ch2)
      return('=');
      else
       return('<');
}
