{
    double v;
    switch(op)
    {
        case '+':v=(double)a+(double)b;break;
        case '-':v=(double)a-(double)b;break;
        case '*':v=(double)a*(double)b;break;
        case '/':v=(double)a/(double)b;break;
    }
    return((StackElementType)v);
}

