        {
            tmp_array[k++] = cards[LPos++];
            tmp_array[k++] = cards[RPos++];
        }

        //cards临时结果
        for (i = 0; i < 2 * n; i++)
            cards[i] = tmp_array[i];
    }

    //保存结果
    for (i = 0; i < 2 * n; i++)
        row[g][i] = tmp_array[i]; 

    //释放空间
    free(cards);
    free(tmp_array);
}

