	//至少需要2根相同长度的火柴
	map&amp;lt;int,int&amp;gt; mapLength;
	map&amp;lt;int,int&amp;gt;::iterator it &amp;#61; mapLength.begin();
	for (int i &amp;#61; 0; i &amp;lt; n; i&amp;#43;&amp;#43;)
	{
