&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-comment"&gt;#include &amp;lt;iostream&amp;gt;&lt;/span&gt;
using namespace std;
int main()
{
    int a, b, c;
    int t;
    cin &amp;gt;&amp;gt; a &amp;gt;&amp;gt; b &amp;gt;&amp;gt; c;
    //a&amp;#xff0c;b&amp;#xff0c;c从小到大排序
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (a &amp;gt; b)
    {
        &lt;span class="hljs-attr"&gt;t&lt;/span&gt; &amp;#61; a;
        &lt;span class="hljs-attr"&gt;a&lt;/span&gt; &amp;#61; b;
        &lt;span class="hljs-attr"&gt;b&lt;/span&gt; &amp;#61; t;
    }
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (a &amp;gt; c)
    {
        &lt;span class="hljs-attr"&gt;t&lt;/span&gt; &amp;#61; a;
        &lt;span class="hljs-attr"&gt;a&lt;/span&gt; &amp;#61; c;
        &lt;span class="hljs-attr"&gt;c&lt;/span&gt; &amp;#61; t;
    }
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (b &amp;gt; c)
    {
        &lt;span class="hljs-attr"&gt;t&lt;/span&gt; &amp;#61; b;
        &lt;span class="hljs-attr"&gt;b&lt;/span&gt; &amp;#61; c;
        &lt;span class="hljs-attr"&gt;c&lt;/span&gt; &amp;#61; t;
    }


    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (a * a &amp;#43; b * &lt;span class="hljs-attr"&gt;b&lt;/span&gt; &amp;#61;&amp;#61; c * c)
        cout &amp;lt;&amp;lt;&lt;span class="hljs-string"&gt;&amp;#34;Right triangle\n&amp;#34;&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (a * a &amp;#43; b * b &amp;gt; c * c)
        cout &amp;lt;&amp;lt;&lt;span class="hljs-string"&gt;&amp;#34;Acute triangle\n&amp;#34;&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
        cout &amp;lt;&amp;lt;&lt;span class="hljs-string"&gt;&amp;#34;Obtuse triangle\n&amp;#34;&lt;/span&gt;;

    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-attr"&gt;a&lt;/span&gt; &amp;#61;&amp;#61; b || &lt;span class="hljs-attr"&gt;a&lt;/span&gt; &amp;#61;&amp;#61; c || &lt;span class="hljs-attr"&gt;b&lt;/span&gt; &amp;#61;&amp;#61; c)
        cout &amp;lt;&amp;lt;&lt;span class="hljs-string"&gt;&amp;#34;Isosceles triangle\n&amp;#34;&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-attr"&gt;a&lt;/span&gt; &amp;#61;&amp;#61; b &amp;amp;&amp;amp; &lt;span class="hljs-attr"&gt;b&lt;/span&gt; &amp;#61;&amp;#61; c)
        cout &amp;lt;&amp;lt;&lt;span class="hljs-string"&gt;&amp;#34;Equilateral triangle\n&amp;#34;&lt;/span&gt;;

    return &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

