&lt;pre&gt;&lt;code class="language-java"&gt;&lt;span class="hljs-comment"&gt;/*
接口
*/&lt;/span&gt;
public interface Method {
    void &lt;span class="hljs-constructor"&gt;CircularArea(&lt;span class="hljs-params"&gt;double&lt;/span&gt; &lt;span class="hljs-params"&gt;r&lt;/span&gt;)&lt;/span&gt;;
    void &lt;span class="hljs-constructor"&gt;TriangleArea(&lt;span class="hljs-params"&gt;double&lt;/span&gt; &lt;span class="hljs-params"&gt;x&lt;/span&gt;, &lt;span class="hljs-params"&gt;double&lt;/span&gt; &lt;span class="hljs-params"&gt;y&lt;/span&gt;, &lt;span class="hljs-params"&gt;double&lt;/span&gt; &lt;span class="hljs-params"&gt;z&lt;/span&gt;)&lt;/span&gt; throws TriangleException;
}
&lt;span class="hljs-comment"&gt;/*
实现类
*/&lt;/span&gt;
public &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; MethodImpl implements Method{

    &amp;#64;Override
    public void &lt;span class="hljs-constructor"&gt;CircularArea(&lt;span class="hljs-params"&gt;double&lt;/span&gt; &lt;span class="hljs-params"&gt;r&lt;/span&gt;)&lt;/span&gt; {
        double p&amp;#61;&lt;span class="hljs-number"&gt;3.14&lt;/span&gt;;
        double area&amp;#61;p*r*r;
        &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;System&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;out.println(area);
    }

    &amp;#64;Override
    public void &lt;span class="hljs-constructor"&gt;TriangleArea(&lt;span class="hljs-params"&gt;double&lt;/span&gt; &lt;span class="hljs-params"&gt;x&lt;/span&gt;, &lt;span class="hljs-params"&gt;double&lt;/span&gt; &lt;span class="hljs-params"&gt;y&lt;/span&gt;, &lt;span class="hljs-params"&gt;double&lt;/span&gt; &lt;span class="hljs-params"&gt;z&lt;/span&gt;)&lt;/span&gt; throws TriangleException {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (x &amp;#43; y &amp;gt; z&lt;span class="hljs-operator"&gt; &amp;amp;&amp;amp; &lt;/span&gt;x &amp;#43; z &amp;gt; y&lt;span class="hljs-operator"&gt; &amp;amp;&amp;amp; &lt;/span&gt;y &amp;#43; z &amp;gt; x) {
            double p &amp;#61; (x &amp;#43; y &amp;#43; z)&lt;span class="hljs-operator"&gt; / &lt;/span&gt;&lt;span class="hljs-number"&gt;2&lt;/span&gt;;
            double area &amp;#61; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Math&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;sqrt(p&lt;span class="hljs-operator"&gt; * &lt;/span&gt;(p - x)&lt;span class="hljs-operator"&gt; * &lt;/span&gt;(p - y)&lt;span class="hljs-operator"&gt; * &lt;/span&gt;(p - z));
            &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;System&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;out.println(area);
        } &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; {
            throw &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-constructor"&gt;TriangleException(&lt;span class="hljs-string"&gt;&amp;#34;你输入的数据不能构成三角形&amp;#34;&lt;/span&gt;)&lt;/span&gt;;
        }
    }
}
&lt;span class="hljs-comment"&gt;/*
自定义异常
*/&lt;/span&gt;
public &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; TriangleException extends Exception{
    public &lt;span class="hljs-constructor"&gt;TriangleException()&lt;/span&gt; {
    }

    public &lt;span class="hljs-constructor"&gt;TriangleException(String &lt;span class="hljs-params"&gt;message&lt;/span&gt;)&lt;/span&gt; {
        super(message);
    }
}
&lt;span class="hljs-comment"&gt;/*
Main测试类
*/&lt;/span&gt;
public &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; Main {
    public static void main(String&lt;span class="hljs-literal"&gt;[]&lt;/span&gt; args) throws TriangleException {
        Method m&amp;#61;&lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-constructor"&gt;MethodImpl()&lt;/span&gt;;
        Scanner sc&amp;#61;&lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-constructor"&gt;Scanner(System.&lt;span class="hljs-params"&gt;in&lt;/span&gt;)&lt;/span&gt;;
        &lt;span class="hljs-comment"&gt;//圆&lt;/span&gt;
        &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;System&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;out.println(&lt;span class="hljs-string"&gt;&amp;#34;请输入圆的半径&amp;#xff1a;&amp;#34;&lt;/span&gt;);
        double r&amp;#61;sc.next&lt;span class="hljs-constructor"&gt;Byte()&lt;/span&gt;;
        &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;System&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;out.print(&lt;span class="hljs-string"&gt;&amp;#34;圆的面积为&amp;#xff1a;&amp;#34;&lt;/span&gt;);
        m.&lt;span class="hljs-constructor"&gt;CircularArea(&lt;span class="hljs-params"&gt;r&lt;/span&gt;)&lt;/span&gt;;
        &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;System&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;out.println(&lt;span class="hljs-string"&gt;&amp;#34;---------------&amp;#34;&lt;/span&gt;);
        &lt;span class="hljs-comment"&gt;//三角形&lt;/span&gt;
        &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;System&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;out.println(&lt;span class="hljs-string"&gt;&amp;#34;请输入三角形边a:&amp;#34;&lt;/span&gt;);
        double x&amp;#61;sc.next&lt;span class="hljs-constructor"&gt;Byte()&lt;/span&gt;;
        &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;System&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;out.println(&lt;span class="hljs-string"&gt;&amp;#34;请输入三角形边b:&amp;#34;&lt;/span&gt;);
        double y&amp;#61;sc.next&lt;span class="hljs-constructor"&gt;Byte()&lt;/span&gt;;
        &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;System&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;out.println(&lt;span class="hljs-string"&gt;&amp;#34;请输入三角形边c:&amp;#34;&lt;/span&gt;);
        double z&amp;#61;sc.next&lt;span class="hljs-constructor"&gt;Byte()&lt;/span&gt;;
        &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;System&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;out.print(&lt;span class="hljs-string"&gt;&amp;#34;三角形的面积为&amp;#xff1a;&amp;#34;&lt;/span&gt;);
        m.&lt;span class="hljs-constructor"&gt;TriangleArea(&lt;span class="hljs-params"&gt;x&lt;/span&gt;,&lt;span class="hljs-params"&gt;y&lt;/span&gt;,&lt;span class="hljs-params"&gt;z&lt;/span&gt;)&lt;/span&gt;;

    }
}
&lt;/code&gt;&lt;/pre&gt;
