                    $bin = intval($bin) | intval($ch) &gt;&gt; 4;
                    $_decode_result .= chr($bin);
                    $bin = ( intval($ch) &amp; 0x0f ) &lt;&lt; 4;
                    break;
                case 2:
                    $bin = intval($bin) | intval($ch) &gt;&gt; 2;
                    $_decode_result .= chr($bin);
                    $bin = ( intval($ch) &amp; 0x03 ) &lt;&lt; 6;
                    break;
                case 3:
                    $bin = intval($bin) | intval($ch);
                    $_decode_result .= chr($bin);
                    break;
            }
            $i++;
        }
        return $_decode_result;
    }
    public function debug($open = true) {
        $this-&gt;_debug = $open;
    }
}
