&lt;div class="post-text" itemprop="text"&gt;
&lt;p&gt;This was already answered in &lt;a href="https://stackoverflow.com/questions/20849591/storing-nested-structs-with-mgo"&gt;Storing nested structs with mgo&lt;/a&gt;,  but it is very easy all you need to do is add &lt;code&gt;bson:",inline"&lt;/code&gt; on the anonymous inner struct and initialize as normal...  &lt;/p&gt;

&lt;p&gt;Here a quick example:&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;package main

import (
    "gopkg.in/mgo.v2"
    "gopkg.in/mgo.v2/bson"
)

type Entity struct {
    Id         bson.ObjectId `json:"id" bson:"_id"`
    InsertedAt time.Time     `json:"inserted_at" bson:"inserted_at"`
    LastUpdate time.Time     `json:"last_update" bson:"last_update"`
}

type User struct {
    Entity    `bson:",inline"`
    Name      string    `json:"name" bson:"name"`
    BirthDate time.Time `json:"birth_date" bson:"birth_date"`
}

func main() {
    info := &amp;amp;mgo.DialInfo{
        Addrs:    []string{"localhost:27017"},
        Timeout:  60 * time.Second,
        Database: "test",
    }

    session, err := mgo.DialWithInfo(info)
    if err != nil {
        panic(err)
    }
    defer session.Close()
    session.SetMode(mgo.Monotonic, true)
    //  c := session.DB("test").C("users")

    user := User{
        Entity:    Entity{"123456789098", time.Now().UTC(), time.Now().UTC()},
        Name:      "John Belushi",
        BirthDate: time.Date(1959, time.February, 28, 0, 0, 0, 0, time.UTC),
    }

    session.DB("test").C("users").Insert(user)
}
&lt;/code&gt;&lt;/pre&gt;
    &lt;/div&gt;

