    return &BarNode{}
}

func NewBaz() *BazNode {
    return &BazNode{}
}

func Append(a, b Node) {
    // set next and prev pointer
    switch v := a.(type) {
    case FooNode:
        v.next = b
    case BarNode:
        v.next = b
    case BazNode:
        v.next = b
    }

    switch v := b.(type) {
    case FooNode:
        v.prev = a
    case BarNode:
        v.prev = a
    case BazNode:
        v.prev = a
