snr &amp;#61; &lt;span class="hljs-number"&gt;20&lt;/span&gt;;  &lt;span class="hljs-comment"&gt;%设定信噪比&amp;#xff0c;单位dB&lt;/span&gt;
noise &amp;#61; &lt;span class="hljs-built_in"&gt;randn&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;size&lt;/span&gt;(x));         &lt;span class="hljs-comment"&gt;% 用randn函数产生高斯白噪声&lt;/span&gt;
Nx &amp;#61; &lt;span class="hljs-built_in"&gt;length&lt;/span&gt;(x);                 &lt;span class="hljs-comment"&gt;% 求出信号x长&lt;/span&gt;
signal_power &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;/Nx*sum(x.*x);  &lt;span class="hljs-comment"&gt;% 求出信号的平均能量&lt;/span&gt;
noise_power &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;/Nx*sum(noise.*noise);  &lt;span class="hljs-comment"&gt;% 求出噪声的能量&lt;/span&gt;
noise_variance &amp;#61; signal_power / ( &lt;span class="hljs-number"&gt;10&lt;/span&gt;^(snr/&lt;span class="hljs-number"&gt;10&lt;/span&gt;) );  &lt;span class="hljs-comment"&gt;% 计算出噪声设定的方差值&lt;/span&gt;
noise &amp;#61; &lt;span class="hljs-built_in"&gt;sqrt&lt;/span&gt;(noise_variance/noise_power)*noise;   &lt;span class="hljs-comment"&gt;% 按噪声的平均能量构成相应的白噪声&lt;/span&gt;
y &amp;#61; x &amp;#43; noise;                  &lt;span class="hljs-comment"&gt;% 合成带噪语音&lt;/span&gt;
[c,l]&amp;#61;wavedec(y,&lt;span class="hljs-number"&gt;3&lt;/span&gt;,&lt;span class="hljs-string"&gt;&amp;#39;db4&amp;#39;&lt;/span&gt;);
[thrl,nkeep]&amp;#61;wdcbm(c,l,&lt;span class="hljs-number"&gt;3&lt;/span&gt;);
&lt;span class="hljs-comment"&gt;% thrl &amp;#61; thselect(y,&amp;#39;rigrsure&amp;#39;);&lt;/span&gt;
[xd1,cxd,lxd,perf0,perfl2] &amp;#61; wdencmp(&lt;span class="hljs-string"&gt;&amp;#39;lvd&amp;#39;&lt;/span&gt;,c,l,&lt;span class="hljs-string"&gt;&amp;#39;db4&amp;#39;&lt;/span&gt;,&lt;span class="hljs-number"&gt;3&lt;/span&gt;,thrl,&lt;span class="hljs-string"&gt;&amp;#39;s&amp;#39;&lt;/span&gt;);
noise_Power0 &amp;#61; sum(&lt;span class="hljs-built_in"&gt;abs&lt;/span&gt;(y&amp;#39;-x&amp;#39;).^&lt;span class="hljs-number"&gt;2&lt;/span&gt;)/&lt;span class="hljs-built_in"&gt;length&lt;/span&gt;(y&amp;#39;-x&amp;#39;);
SNR0 &amp;#61; &lt;span class="hljs-number"&gt;10&lt;/span&gt;*&lt;span class="hljs-built_in"&gt;log10&lt;/span&gt;(signal_power/noise_Power0);
&lt;span class="hljs-built_in"&gt;disp&lt;/span&gt;([&lt;span class="hljs-string"&gt;&amp;#39;带噪语音信噪比&amp;#xff1a;&amp;#39;&lt;/span&gt;,num2str(SNR0)])
noise_Power2 &amp;#61; sum(&lt;span class="hljs-built_in"&gt;abs&lt;/span&gt;(xd1-x).^&lt;span class="hljs-number"&gt;2&lt;/span&gt;)/&lt;span class="hljs-built_in"&gt;length&lt;/span&gt;(xd1-x);  &lt;span class="hljs-comment"&gt;%&amp;#xff08;带噪信号-纯信号&amp;#xff09;的平方&lt;/span&gt;
SNR1 &amp;#61; &lt;span class="hljs-number"&gt;10&lt;/span&gt;*&lt;span class="hljs-built_in"&gt;log10&lt;/span&gt;(signal_power/noise_Power2);
&lt;span class="hljs-built_in"&gt;disp&lt;/span&gt;([&lt;span class="hljs-string"&gt;&amp;#39;db4小波软阈值法信噪比&amp;#xff1a;&amp;#39;&lt;/span&gt;,num2str(SNR1)])
subplot(&lt;span class="hljs-number"&gt;4&lt;/span&gt;,&lt;span class="hljs-number"&gt;2&lt;/span&gt;,&lt;span class="hljs-number"&gt;1&lt;/span&gt;)
&lt;span class="hljs-built_in"&gt;plot&lt;/span&gt;(x);
subplot(&lt;span class="hljs-number"&gt;4&lt;/span&gt;,&lt;span class="hljs-number"&gt;2&lt;/span&gt;,&lt;span class="hljs-number"&gt;2&lt;/span&gt;)
spectrogram(x,win,overlap,ffTLength,Fs,&lt;span class="hljs-string"&gt;&amp;#39;yaxis&amp;#39;&lt;/span&gt;); 
title(&lt;span class="hljs-string"&gt;&amp;#39;纯净信号语谱图&amp;#39;&lt;/span&gt;)
subplot(&lt;span class="hljs-number"&gt;4&lt;/span&gt;,&lt;span class="hljs-number"&gt;2&lt;/span&gt;,&lt;span class="hljs-number"&gt;3&lt;/span&gt;)
&lt;span class="hljs-built_in"&gt;plot&lt;/span&gt;(y);
subplot(&lt;span class="hljs-number"&gt;4&lt;/span&gt;,&lt;span class="hljs-number"&gt;2&lt;/span&gt;,&lt;span class="hljs-number"&gt;4&lt;/span&gt;)
spectrogram(y,win,overlap,ffTLength,Fs,&lt;span class="hljs-string"&gt;&amp;#39;yaxis&amp;#39;&lt;/span&gt;);
title(&lt;span class="hljs-string"&gt;&amp;#39;带噪信号语谱图&amp;#39;&lt;/span&gt;)
subplot(&lt;span class="hljs-number"&gt;4&lt;/span&gt;,&lt;span class="hljs-number"&gt;2&lt;/span&gt;,&lt;span class="hljs-number"&gt;5&lt;/span&gt;)
&lt;span class="hljs-built_in"&gt;plot&lt;/span&gt;(xd1);
subplot(&lt;span class="hljs-number"&gt;4&lt;/span&gt;,&lt;span class="hljs-number"&gt;2&lt;/span&gt;,&lt;span class="hljs-number"&gt;6&lt;/span&gt;)
spectrogram(xd1,win,overlap,ffTLength,Fs,&lt;span class="hljs-string"&gt;&amp;#39;yaxis&amp;#39;&lt;/span&gt;); 
title(&lt;span class="hljs-string"&gt;&amp;#39;db4小波软阈值去噪语谱图&amp;#39;&lt;/span&gt;)
subplot(&lt;span class="hljs-number"&gt;4&lt;/span&gt;,&lt;span class="hljs-number"&gt;2&lt;/span&gt;,&lt;span class="hljs-number"&gt;7&lt;/span&gt;)
[xd2,cxd2,lxd2,perf1,perf22] &amp;#61; wdencmp(&lt;span class="hljs-string"&gt;&amp;#39;lvd&amp;#39;&lt;/span&gt;,c,l,&lt;span class="hljs-string"&gt;&amp;#39;db4&amp;#39;&lt;/span&gt;,&lt;span class="hljs-number"&gt;3&lt;/span&gt;,thrl,&lt;span class="hljs-string"&gt;&amp;#39;h&amp;#39;&lt;/span&gt;);
noise_Power2 &amp;#61; sum(&lt;span class="hljs-built_in"&gt;abs&lt;/span&gt;(xd2-x).^&lt;span class="hljs-number"&gt;2&lt;/span&gt;)/&lt;span class="hljs-built_in"&gt;length&lt;/span&gt;(xd2-x);  &lt;span class="hljs-comment"&gt;%&amp;#xff08;带噪信号-纯信号&amp;#xff09;的平方&lt;/span&gt;
SNR2 &amp;#61; &lt;span class="hljs-number"&gt;10&lt;/span&gt;*&lt;span class="hljs-built_in"&gt;log10&lt;/span&gt;(signal_power/noise_Power2);
&lt;span class="hljs-built_in"&gt;disp&lt;/span&gt;([&lt;span class="hljs-string"&gt;&amp;#39;db4小波硬阈值法信噪比&amp;#xff1a;&amp;#39;&lt;/span&gt;,num2str(SNR2)])
&lt;span class="hljs-built_in"&gt;plot&lt;/span&gt;(xd2);
subplot(&lt;span class="hljs-number"&gt;4&lt;/span&gt;,&lt;span class="hljs-number"&gt;2&lt;/span&gt;,&lt;span class="hljs-number"&gt;8&lt;/span&gt;)
spectrogram(xd2,win,overlap,ffTLength,Fs,&lt;span class="hljs-string"&gt;&amp;#39;yaxis&amp;#39;&lt;/span&gt;); 
title(&lt;span class="hljs-string"&gt;&amp;#39;db4小波硬阈值去噪语谱图&amp;#39;&lt;/span&gt;)
sgtitle(&lt;span class="hljs-string"&gt;&amp;#39;dbN小波去噪&amp;#39;&lt;/span&gt;)
&lt;span class="hljs-built_in"&gt;disp&lt;/span&gt;([&lt;span class="hljs-string"&gt;&amp;#39;db4小波软阈值去噪后语音成分能量&amp;#xff1a;&amp;#39;&lt;/span&gt;,num2str(sum(&lt;span class="hljs-built_in"&gt;abs&lt;/span&gt;(xd1-(y-x)))),&lt;span class="hljs-string"&gt;&amp;#39;dB&amp;#39;&lt;/span&gt;])
&lt;span class="hljs-built_in"&gt;disp&lt;/span&gt;([&lt;span class="hljs-string"&gt;&amp;#39;db4小波硬阈值去噪后语音成分能量&amp;#xff1a;&amp;#39;&lt;/span&gt;,num2str(sum(&lt;span class="hljs-built_in"&gt;abs&lt;/span&gt;(xd2-(y-x)))),&lt;span class="hljs-string"&gt;&amp;#39;dB&amp;#39;&lt;/span&gt;])
&lt;/code&gt;&lt;/pre&gt;
