    &lt;span class="hljs-type"&gt;FILE&lt;/span&gt; &lt;span class="hljs-operator"&gt;*&lt;/span&gt;p2 &lt;span class="hljs-operator"&gt;&amp;#61;&lt;/span&gt; fopen(&lt;span class="hljs-string"&gt;&amp;#34;tmp.txt&amp;#34;&lt;/span&gt;,&lt;span class="hljs-string"&gt;&amp;#34;w&amp;#34;&lt;/span&gt;);
