    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(fgets(buf, &lt;span class="hljs-number"&gt;100&lt;/span&gt;, p1) &lt;span class="hljs-operator"&gt;!&amp;#61;&lt;/span&gt; &lt;span class="hljs-type"&gt;NULL&lt;/span&gt;)
