        float min &lt;span class="hljs-operator"&gt;&amp;#61;&lt;/span&gt; &lt;span class="hljs-number"&gt;999&lt;/span&gt;, max &lt;span class="hljs-operator"&gt;&amp;#61;&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;

        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(int k &lt;span class="hljs-operator"&gt;&amp;#61;&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; k &lt;span class="hljs-operator"&gt;&amp;lt;&lt;/span&gt; &lt;span class="hljs-number"&gt;10&lt;/span&gt;; k&lt;span class="hljs-operator"&gt;&amp;#43;&amp;#43;&lt;/span&gt;)
