template<typename T>
T maxn(const T * t,int size){
    T max = t[0];
    for(int i = 1;i < size; i++)
        if(*(t+i)>max)
            max = *(t + i);
    return max;
}
template <char *> char* maxn(const char* t[], int size){
    char* max = t[0];
    for(int i = 1 ; i < size ;i++){
        if(strlen(max) < strlen(t[i])){
            cout << endl << max << " " << t[i] << endl;
            strcpy(max,t[i]);           
        }
    }
    return max;
}

