            }
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (t &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;-1&lt;/span&gt;)
            {
                &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; j &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; j &amp;lt;&amp;#61; b; j&amp;#43;&amp;#43;)
                {
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; ((i &amp;#43; j) &amp;#61;&amp;#61; m)
                    {
