	printf(&amp;#34;是否重新设置(Y/N)?&amp;#34;);
	scanf(&amp;#34;%c&amp;#34;,&amp;amp;ch);
	if (ch &amp;#61;&amp;#61; &amp;#39;Y&amp;#39; || ch &amp;#61;&amp;#61; &amp;#39;y&amp;#39;)
	{
		printf(&amp;#34;请输入石子总数:&amp;#34;);
		while(1)
		{
			scanf(&amp;#34;%d&amp;#34;,&amp;amp;n);
			if(n &amp;lt;&amp;#61; 0)
			{
				printf(&amp;#34;石子数大于0&amp;#xff0c;请重新输入&amp;#xff1a;&amp;#34;);
			}else
				break;
		}
		*total &amp;#61; n;
		system(&amp;#34;cls&amp;#34;);
		printf(&amp;#34;设置成功&amp;#xff01;\n&amp;#34;);
		printf(&amp;#34;当前石子总数:%d\n&amp;#34;,*total);
		printf(&amp;#34;按任意键返回上一层...&amp;#34;);
		getchar();
		getchar();
	}
}
//取石子&amp;#xff0c;返回取石子的个数
int GetStone(int x)
{
	int n;
	printf(&amp;#34;请输入取石子的个数:&amp;#34;);
	while(1)
	{
		scanf(&amp;#34;%d&amp;#34;,&amp;amp;n);
		if (n&amp;lt;1 || n&amp;gt; 3)
		{
			printf(&amp;#34;石子数量只能是1、2或3&amp;#xff0c;请重新输入:&amp;#34;);
		}else if( n &amp;gt; x)
			printf(&amp;#34;取的石子数大于剩余石子数&amp;#xff0c;请重新输入:&amp;#34;);
		else
			break;
	}
	return n;
}


//开始游戏
void StartGame(int total)
{
	int a,b;
	int left &amp;#61; total;
	while(1)
	{
		system(&amp;#34;cls&amp;#34;);
		printf(&amp;#34;当前剩余石子数:%d\n&amp;#34;,left);
		printf(&amp;#34;A:&amp;#34;);
		a &amp;#61; GetStone(left);
		left -&amp;#61; a;
		if(left &amp;#61;&amp;#61; 0)
		{
			printf(&amp;#34;A取到最后1个石子&amp;#xff0c;A输\n&amp;#34;);
			break;
		}
		printf(&amp;#34;B:&amp;#34;);
		b &amp;#61; GetStone(left);
		left -&amp;#61; b;
		if(left &amp;#61;&amp;#61; 0)
		{
			printf(&amp;#34;B取到最后1个石子&amp;#xff0c;B输\n&amp;#34;);
			break;
		}
	}
}



int main()
{
	int opt;
	int total &amp;#61; 100; //石头总数
	int bgo &amp;#61; 1;
	while(bgo)
	{
		system(&amp;#34;cls&amp;#34;);//清屏
