		printf(&amp;#34;|     1.设置                       |\n&amp;#34;);
		printf(&amp;#34;|     2.开始游戏                   |\n&amp;#34;);
		printf(&amp;#34;|     3.退出                       |\n&amp;#34;);
		printf(&amp;#34;------------------------------------\n&amp;#34;);

		scanf(&amp;#34;%d&amp;#34;,&amp;amp;opt);
		getchar();
		switch(opt)
		{
		case 1:
			Set(&amp;amp;total);
			break;
		case 2:
			StartGame(total);
			break;
		case 3:
			bgo &amp;#61; 0;
			break;
		}


	}
	return 0;
}
