&lt;span class="hljs-keyword"&gt;res&lt;/span&gt; &amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;\n&amp;#34;&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;join&lt;/span&gt;(content)
&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-python"&gt;
&lt;span class="hljs-keyword"&gt;class&lt;/span&gt; Solution:
    def &lt;span class="hljs-number"&gt;__&lt;/span&gt;init&lt;span class="hljs-number"&gt;__&lt;/span&gt;(self) -&amp;gt; None:
        self.choice &amp;#61; {&lt;!-- --&gt;&lt;span class="hljs-string"&gt;&amp;#34;Rock&amp;#34;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#34;Scissors&amp;#34;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#34;Paper&amp;#34;&lt;/span&gt;}

    def Game(self, N: int) -&amp;gt; None:
        &lt;span class="hljs-string"&gt;&amp;#34;&amp;#34;&amp;#34;
        :param N:
        :return:
        &amp;#34;&amp;#34;&amp;#34;&lt;/span&gt;

        def decision(way&lt;span class="hljs-number"&gt;1&lt;/span&gt;: str, way&lt;span class="hljs-number"&gt;2&lt;/span&gt;: str) -&amp;gt; str:
            &lt;span class="hljs-string"&gt;&amp;#34;&amp;#34;&amp;#34;
            :param way1:
            :param way2:
            :return:
            &amp;#34;&amp;#34;&amp;#34;&lt;/span&gt;
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; way&lt;span class="hljs-number"&gt;1&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;Rock&amp;#34;&lt;/span&gt;:
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; way&lt;span class="hljs-number"&gt;2&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;Rock&amp;#34;&lt;/span&gt;:
                    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;Tie&amp;#34;&lt;/span&gt;
                elif way&lt;span class="hljs-number"&gt;2&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;Scissors&amp;#34;&lt;/span&gt;:
                    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;Player1&amp;#34;&lt;/span&gt;
                &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;:
                    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;Player2&amp;#34;&lt;/span&gt;
            elif way&lt;span class="hljs-number"&gt;1&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;Scissors&amp;#34;&lt;/span&gt;:
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; way&lt;span class="hljs-number"&gt;2&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;Scissors&amp;#34;&lt;/span&gt;:
                    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;Tie&amp;#34;&lt;/span&gt;
                elif way&lt;span class="hljs-number"&gt;2&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;Rock&amp;#34;&lt;/span&gt;:
                    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;Player2&amp;#34;&lt;/span&gt;
                &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;:
                    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;Player1&amp;#34;&lt;/span&gt;
            &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;:
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; way&lt;span class="hljs-number"&gt;2&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;Paper&amp;#34;&lt;/span&gt;:
                    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;Tie&amp;#34;&lt;/span&gt;
                elif way&lt;span class="hljs-number"&gt;2&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;Scissors&amp;#34;&lt;/span&gt;:
                    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;Player2&amp;#34;&lt;/span&gt;
                &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;:
                    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;Player1&amp;#34;&lt;/span&gt;

        arr &amp;#61; []
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; i &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; range(N):
            choices &amp;#61; input()
            choices&lt;span class="hljs-number"&gt;1&lt;/span&gt;, choices&lt;span class="hljs-number"&gt;2&lt;/span&gt; &amp;#61; choices.split(&lt;span class="hljs-string"&gt;&amp;#39; &amp;#39;&lt;/span&gt;)
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; choices&lt;span class="hljs-number"&gt;1&lt;/span&gt; not &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; self.choice:
                print(&lt;span class="hljs-string"&gt;&amp;#34;Input Error, Enter Again&amp;#34;&lt;/span&gt;)
                i -&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;
                &lt;span class="hljs-keyword"&gt;continue&lt;/span&gt;
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; choices&lt;span class="hljs-number"&gt;2&lt;/span&gt; not &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; self.choice:
                print(&lt;span class="hljs-string"&gt;&amp;#34;Input Error, Enter Again&amp;#34;&lt;/span&gt;)
