 #include &lt;iostream&gt; 
using namespace std;
int largestFib(int n){
	if(n==0||n==1){
		return n;
	} 
	int a=0;
	int b=1;
	int c=1;
	while(c&lt;=n){
		a=b;
		b=c;
		c=a+b;
	}
	return b;
}
int main(){
	int n;
	cin&gt;&gt;n;
	while(n&gt;0){
		int tempn=largestFib(n);
		cout&lt;&lt;tempn&lt;&lt;" ";
		n=n-tempn;
	}
	
}
