Answer:
Just to provide the code that actually works for me (written in c++), the answer below is largely the same to the one posted by Vaibhav.
```
#include <algorithm>
#include <iostream>
#include <vector>
using namespace std;

bool check(vector<int> v, int maxDays, int req) {
    int sum = 0;

    for (int i = 0; i < maxDays; i++) {
        sum += v[i];
        if (sum >= req) return true;
    }

    int days = 1, cnt = 1;

    for (int i = maxDays; i < v.size(); i++){
        if (days > maxDays){
            days = 1;
            cnt++;
        }
        if (v[i] - cnt <= 0) break;
        sum += v[i] - cnt;
        if (sum >= req) break;
        days++;
    }

    return sum >= req;
}

int main() {
    int n, m;
    cin >> n >> m;

    vector<int> v(n);

    for (int i = 0; i < n; i++)
        cin >> v[i];

    sort(v.begin(), v.end(), greater<int>());

    int ans = -1, start = 0, end = n;

    while (start <= end) {
        int mid = start + (end - start) / 2;

        if (check(v, mid, m)) {
            ans = mid;
            end = mid - 1;
        } else {
            start = mid + 1;
        }
    }

    cout << ans << "\n";
    return 0;
}
