    $stringG = "green";
    $stringR = "red";
    $stringB = "blue";
    $stringO = "orange";
    $clear = "";
    $statusFile = "status.txt";

    //erase file
    $fh = fopen($statusFile, 'w');  //clear the file with "clear"
    fwrite($fh, $clear);
    fclose($fh);

    //Insert LOOP
    $counter = 0;
    while ( $counter <= 10 ) {
    //echo "loop begun";

    // Read THE FILE
    $fh = fopen($statusFile, 'r');
    $data = fread($fh, filesize($statusFile));
    fclose($fh);


    //process the file
